from typing import Optional

from dataclasses_json import dataclass_json
from pydantic.dataclasses import dataclass

from .deviceinterface import DeviceInterface
from .location import Location


@dataclass_json
@dataclass
class Device:
    identifier: str
    hostname: str
    hostname_full: str
    location: Location
    version: Optional[str]
    status: str
    vendor: str
    model: str
    function: str
    mgmt_interface_id: Optional[str]
    mgmt_interface: Optional[DeviceInterface]
