from typing import Optional

from dataclasses_json import dataclass_json
from pydantic.dataclasses import dataclass


@dataclass_json
@dataclass
class Organization:
    address: str
    city: str
    country: str
    currency: str
    customer_id: str
    demo: bool
    identifier: str
    locale: str
    login_domain: str
    mandant: Optional[str]
    name: str
    name_slug: str
    reseller: Optional[str]
    phone: str
    mobile_phone: Optional[str]
    zip: str
    vat_id: Optional[str]
