from typing import Optional

from dataclasses_json import dataclass_json
from pydantic.dataclasses import dataclass


@dataclass_json
@dataclass
class Policy:
    identifier: str
    name: str
    is_group: bool
    pattern: Optional[list]
    assigned: int
    assigned_devices: list
    assigned_groups: list
    peering_name: Optional[str]
    session_name: Optional[str]
    is_scnd_policy: bool
