import ipaddress


def ipaddr_to_json_encoder(input):
    """
    This encodes IP data TO json. Write address as string or None.
    """
    if input:
        return str(input)
    else:
        return None


def json_to_ipaddr_decoder(input):
    if input:
        return ipaddress.ip_network(input)
    else:
        return None


class PydanticConfig:
    """
    Pydantic Config to support Arbitrary Types like ipaddress.* which have no validators
    """

    arbitrary_types_allowed = True
