import datetime
from typing import Optional

from dataclasses_json import dataclass_json
from pydantic.dataclasses import dataclass


@dataclass_json
@dataclass
class VLANRole:
    name: str
    description: str
    entity: str
    is_system: bool
    identifier: str


@dataclass_json
@dataclass
class VLAN:
    identifier: str
    name: str
    description_customer: str
    description_internal: Optional[str]
    ticket: str
    created: datetime.datetime
    updated: datetime.datetime
    public_id: int
    role: VLANRole
    status: int
    status_text: str
